    @Test
    public void testJoke() throws IOException {
        List<String> jokes = Reader.read("./joke.txt");
        String request = "/joke";
        for (int i = 0; i < 10; i++) {
            Assert.assertEquals(jokes.get(i), commands.start(request, "777"));
        }
    }
